field.value:
// process cia:Survey
//['IN_PROGRESS'];
var arguments = new com.sirma.itt.seip.context.Context();
arguments.put('todayPlus', 1);
arguments.put('type', 'cia:Survey');
arguments.put('statement', '?instance emf\\:status \"IN_PROGRESS\" . ');
var ciaSurvey = search.with(search.buildArgumentsForPredefinedQuery('ciaQueries/getInstanceNearEnd', arguments));

var assigneesProperties = ['assignee', 'emf:hasAssignee', 'createdBy', 'emf:createdBy', 'hasWatcher', 'emf:hasWatcher', 'hasSupervisor', 'chc:hasSupervisor', 'hasParticipant', 'ptop:hasParticipant'];

for(var i=0; i < ciaSurvey.length; i++){
	var subject ='(Upcoming Due date) '+ ciaSurvey[i].get('title');
	var recipients = tasks.getTaskAssignees(ciaSurvey[i] ,assigneesProperties);
	mail.sendNotifications(ciaSurvey[i], security.getCurrentLoggedUser(), subject, "email_endDate_object_cia", recipients, null);
}
