field.value:
// process chc:ConservationActivity
//['IN_PROGRESS'];
var arguments = new com.sirma.itt.seip.context.Context();
arguments.put('todayPlus', 1);
arguments.put('type', 'chc:ConservationActivity');
arguments.put('statement', '?instance emf\\:status \"IN_PROGRESS\" . ');
var chcConservationActivity = search.with(search.buildArgumentsForPredefinedQuery('ngaQueries/getInstanceNearEnd', arguments));

var assigneesProperties = ['assignee', 'emf:hasAssignee', 'createdBy', 'emf:createdBy', 'emf:hasWatcher', 'chc:hasSupervisor', 'ptop:hasParticipant'];

for(var i=0; i < chcConservationActivity.length; i++){
	var subject ='(Upcoming Due date) '+ chcConservationActivity[i].get('title');
	var recipients = tasks.getTaskAssignees(chcConservationActivity[i] ,assigneesProperties);
	mail.sendNotifications(chcConservationActivity[i], security.getCurrentLoggedUser(), subject, "email_endDate_object", recipients, null);
}
