field.value:
// process emf:BusinessProcess
// state IN_PROGRESS, NOT_STARTED
var arguments = new com.sirma.itt.seip.context.Context();
arguments.put('todayPlus', 1);
arguments.put('type', 'emf:BusinessProcess');
arguments.put('statement', '{?instance emf\\:status \\"IN_PROGRESS\\" . } UNION {?instance emf\\:status \\"NOT_STARTED\\" . }');
var emfBusinessProcess = search.with(search.buildArgumentsForPredefinedQuery('ngaQueries/getInstanceNearEnd', arguments));

var assigneesProperties = ['assignee', 'emf:hasAssignee', 'createdBy', 'emf:createdBy', 'hasWatcher', 'emf:hasWatcher' ];

for(var i=0; i < emfBusinessProcess.length; i++){
	var subject ='(Upcoming Due date) '+ codelist.getDescription(234, emfBusinessProcess[i].get('type'), 'en');
	var recipients = tasks.getTaskAssignees(emfBusinessProcess[i] ,assigneesProperties);
	mail.sendNotifications(emfBusinessProcess[i], security.getCurrentLoggedUser(), subject, "email_endDate_object", recipients, null);
}
