field.value:
// process emf:Case
//['IN_PROGRESS', 'ON_HOLD'];
var arguments = new com.sirma.itt.seip.context.Context();
arguments.put('todayPlus', 1);
arguments.put('type', 'emf:Case');
arguments.put('statement', '{?instance emf\\:status \\"IN_PROGRESS\\" . } UNION { ?instance emf\\:status \\"ON_HOLD\\" . }');
var emfCase = search.with(search.buildArgumentsForPredefinedQuery('smkQueries/getInstanceNearEnd', arguments));

var assigneesProperties = ['assignee', 'emf:hasAssignee', 'createdBy', 'emf:createdBy', 'hasWatcher', 'emf:hasWatcher', 'hasParticipant', 'ptop:hasParticipant'];

for(var i=0; i < emfCase.length; i++){
	var subject ='(Upcoming Due date) '+ emfCase[i].get('title');
	var recipients = tasks.getTaskAssignees(emfCase[i] ,assigneesProperties);
	mail.sendNotifications(emfCase[i], security.getCurrentLoggedUser(), subject, "email_endDate_object_smk", recipients, null);
}
