field.value:
// process chc:Exhibition
// ['IN_PROGRESS'];
var arguments = new com.sirma.itt.seip.context.Context();
arguments.put('todayPlus', 1);
arguments.put('type', 'chc:Exhibition');
arguments.put('statement', '{?instance emf\\:status \\"IN_PROGRESS\\" . }');
var chcExhibition = search.with(search.buildArgumentsForPredefinedQuery('smkQueries/getInstanceNearEnd', arguments));

var assigneesProperties = ['createdBy', 'emf:createdBy', 'hasWatcher', 'emf:hasWatcher'];

for(var i=0; i < chcExhibition.length; i++){
	var subject ='(Upcoming Due date) '+ chcExhibition[i].get('title');
	var recipients = tasks.getTaskAssignees(chcExhibition[i] ,assigneesProperties);
	mail.sendNotifications(chcExhibition[i], security.getCurrentLoggedUser(), subject, "email_endDate_object_smk", recipients, null);
}
