field.value:
// process emf:Project
// ['IN_PROGRESS'];
var arguments = new com.sirma.itt.seip.context.Context();
arguments.put('todayPlus', 1);
arguments.put('type', 'emf:Project');
arguments.put('statement', '{?instance emf\\:status \\"IN_PROGRESS\\" . } UNION { ?instance emf\\:status \\"ON_HOLD\\" . }');
var emfProject = search.with(search.buildArgumentsForPredefinedQuery('smkQueries/getInstanceNearEnd', arguments));

var assigneesProperties = ['assignee', 'emf:hasAssignee', 'createdBy', 'emf:createdBy', 'hasWatcher', 'emf:hasWatcher', 'hasParticipant', 'ptop:hasParticipant'];

for(var i=0; i < emfProject.length; i++){
	var subject ='(Upcoming Due date) '+ emfProject[i].get('title');
	var recipients = tasks.getTaskAssignees(emfProject[i] ,assigneesProperties);
	mail.sendNotifications(emfProject[i], security.getCurrentLoggedUser(), subject, "email_endDate_object_smk", recipients, null);
}
